mapper.insert:
insert into member 
	(id, pw, name, email, addr, birthday, hp)
	values
	(
		#{id}
		,#{pw}
		,#{name}
		,#{email}
		,#{addr}
		,#{birthdate}
		,#{hp}
	)

mapper.update:
update member set
	reg_dt = now()

		,name = #{name}


		,email = #{email}


		,addr = #{addr}


		,birthdate = #{birthdate}


		,hp = #{hp}

where
	id = #{id}
